         * @param {Object} e 
         */
        page_change: function page_change(e) {
            var n = Number(e.target.value);
            if (isNaN(n)) {
                n = 1;
            }
            if (n < 1) {
                n = 1;
            } else if (n > this.page_count) {
                n = this.page_count
            }
            this.page_now = n;
        },

        /**
         * 
         * @param {Array} list 
         * @param {String} arr_str id
         * @param {String} key 
         * @param {String} name 
         * @param {String} span 
         */
        get_name(list, arr_str, key, name, span) {
            if (!name) {
                name = "name";
            }
            var value = "";
            if (arr_str) {
                if (typeof(arr_str) == 'string') {
                    if (!span) {
                        span = ',';
                    }
                    var arr = arr_str.split(span);
                    var id = Number(arr[0]);

                    for (var i = 0; i < list.length; i++) {
                        var o = list[i];
                        if (o[key] == id) {
                            value += '|' + o[name];
                        }
                    }
                } else {
                    var id = arr_str;
                    for (var i = 0; i < list.length; i++) {
                        var o = list[i];
                        if (o[key] == id) {
                            value = o[name];
                            break
                        }
                    }
                }
            }
            return value.replace('|', '');
        },

