        /**
         * , 5
         * @param {Number} fid ID
         * @param {Object} list 
         * @return {Number} 
         */
        opens_lv: function opens_lv(fid, list) {
            if (!list) {
                list = this.list;
            }
            var lv = 0;
            var father_id = this.father_id;
            var id = this.field;
            var num = fid;
            for (var n = 0; n < 5; n++) {
                if (num === 0) {
                    break;
                }
                for (var i = 0; i < list.length; i++) {
                    var o = list[i];
                    if (o[id] === num) {
                        lv++;
                        num = o[father_id];
                        if (num === 0) {
                            break;
                        }
                    }
                }
            }
            return lv;
        },

